import React, { Component } from "react";
import "./App.css";

//Material UI
import { withStyles } from "@material-ui/core/styles";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";

//Logo
import Larger from "./Larger.png";
import Woman from "./Woman.jpg";
import DownloadApp from "./DownloadApp.png";
import TextField from "@material-ui/core/TextField";
import Github from "./Github.png";
import Reddit from "./Reddit.png";
import Barrels from "./Barrels.png";

const useStyles = (theme) => ({
  root: {
    overflowY: "scroll",
    height: "100%",
    width: "100%",
    position: "absolute",
  },
  sectionTop: {
    width: "100%",
    height: "65%",
    backgroundImage: `url(${Woman})`,
    backgroundSize: "contain",
    // backgroundRepeat: "no-repeat",
    position: "static",
    borderBottom: "1px solid grey",
    margin: 0,
  },
  sectionDark: {
    width: "100%",
    height: "auto",
    backgroundColor: "#f2f2f2",
    position: "static",
    borderBottom: "1px solid grey",
    margin: 0,
  },
  sectionTest: {
    width: "100%",
    height: "auto",
    backgroundColor: "#f2f2f2",
    position: "relative",
    borderBottom: "1px solid grey",
    margin: 0,
  },
  sectionLight: {
    width: "100%",
    height: "65%",
    backgroundColor: "#ffffff",
    position: "static",
    borderBottom: "1px solid grey",
    margin: 0,
  },
  avatar: {
    height: 70,
    width: 70,
    zIndex: 1,
    marginRight: 30,
  },
});

export class Landing extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isDesktop: false,
    };
    this.Features = React.createRef();
    this.updatePredicate = this.updatePredicate.bind(this);
  }
  componentDidMount() {
    this.updatePredicate();
    window.addEventListener("resize", this.updatePredicate);
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.updatePredicate);
  }

  updatePredicate() {
    this.setState({ isDesktop: window.innerWidth > 716 });
  }

  handleOnClick = (event) => {
    //.current is verification that your element has rendered
    if (this.Features.current) {
      this.Features.current.scrollIntoView({
        behavior: "smooth",
        block: "nearest",
      });
    }
  };

  render() {
    const isDesktop = this.state.isDesktop; //break at 716 width, when barrel cash stacks
    const { classes } = this.props;

    return (
      <div
        style={{
          overflowY: "scroll",
          height: "100%",
          width: "100%",
          position: "absolute",
        }}
        className="scroll"
      >
        <div className={classes.sectionDark}>
          <div
            style={{
              width: "80%",
              marginLeft: "auto",
              marginRight: "auto",
              marginBottom: 50,
              maxWidth: 1189,
            }}
          >
            <div
              style={{
                display: "flex",
                // marginLeft: "auto",
                // marginRight: "auto",
                // justifyContent: "center",
                // alignItems: "center",
                // alignContent: "center",
                paddingTop: 20,
                // position: "absolute",
              }}
