import React, { Component } from "react";

//Material UI
import { withStyles } from "@material-ui/core/styles";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";

//Logo
import Larger from "./Larger.png";
import Woman from "./Woman.jpg";
import DownloadApp from "./DownloadApp.png";
import TextField from "@material-ui/core/TextField";

const useStyles = (theme) => ({
  root: {
    overflowY: "scroll",
    height: "100%",
    width: "100%",
    position: "absolute",
  },
  sectionTop: {
    width: "100%",
    height: "65%",
    backgroundImage: `url(${Woman})`,
    backgroundSize: "contain",
    // backgroundRepeat: "no-repeat",
    position: "static",
    borderBottom: "1px solid grey",
    margin: 0,
  },
  sectionDark: {
    width: "100%",
    height: "auto",
    backgroundColor: "#f2f2f2",
    position: "static",
    borderBottom: "1px solid grey",
    margin: 0,
  },
  sectionTest: {
    width: "100%",
    height: "auto",
    backgroundColor: "#f2f2f2",
    position: "relative",
    borderBottom: "1px solid grey",
    margin: 0,
  },
  sectionLight: {
    width: "100%",
    height: "65%",
    backgroundColor: "#ffffff",
    position: "static",
    borderBottom: "1px solid grey",
    margin: 0,
  },
  avatar: {
    height: 70,
    width: 70,
    zIndex: 1,
    marginRight: 30,
  },
});

export class Landing extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isDesktop: false,
    };
    this.updatePredicate = this.updatePredicate.bind(this);
  }
  componentDidMount() {
    this.updatePredicate();
    window.addEventListener("resize", this.updatePredicate);
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.updatePredicate);
  }

  updatePredicate() {
    this.setState({ isDesktop: window.innerWidth > 850 });
  }

  render() {
    const isDesktop = this.state.isDesktop;
    const { classes } = this.props;

    return (
      <div className={classes.root}>
        <div className={classes.sectionDark}>
          <div style={{ width: "80%", paddingLeft: "10%", marginBottom: 50 }}>
            <div
              style={{
                display: "flex",
                // marginLeft: "auto",
                // marginRight: "auto",
                // justifyContent: "center",
                // alignItems: "center",
                // alignContent: "center",
                paddingTop: 20,
                // position: "absolute",
              }}
            >
              <Avatar className={classes.avatar} src={Larger} alt="logo" />
              <h2 style={{ marginRight: 30 }}>Barrel Cash</h2>
              <h2 style={{ marginRight: 30 }}>Features</h2>

              <h2 style={{ marginRight: 30 }}>Documents</h2>
              <h2>FAQ</h2>
            </div>
            <Typography variant="h2" style={{ marginTop: 30 }}>
              Budget easily on IOS
            </Typography>
            <Typography style={{ marginTop: 20 }}>
              Barrel Cash is a beautiful, simple way to budget monthly expenses.
            </Typography>
            <div style={{ width: 144, height: 48, marginTop: 40 }}>
              <a
                target="_blank"
                href="https://apps.apple.com/us/app/bear-beautiful-writing-app/id1016366447?ls=1"
              >
                <img
                  alt="Download Barrel Cash on the App Store"
                  style={{
                    width: "100%",
                    height: "100%",
                    position: "relative",
                  }}
                  src={DownloadApp}
                />
              </a>
            </div>

            <div style={{ display: "flex", marginTop: 30 }}>
              <form noValidate autoComplete="off">
                <TextField
                  id="outlined-basic"
                  label="Email"
                  variant="outlined"
                  style={{ width: 400, fontSize: 10 }}
                />
              </form>
              <div style={{ marginLeft: 20 }}>
                <Button
                  variant="contained"
                  color="primary"
                  style={{
                    height: "100%",
                    width: 200,
                    backgroundColor: "#a7e8b9",
                    color: "#000000",
                  }}
                >
                  Subscribe
                </Button>
              </div>
            </div>
            <Typography style={{ marginTop: 20 }}>
              Get updates on Barrel Cash. Never spam.
            </Typography>
          </div>
        </div>
        <div className={classes.sectionTest}>
          <div
            style={{
              width: "80%",
              paddingLeft: "10%",
              marginBottom: 50,
            }}
          >
            <div
              style={{
                display: "flex",
                width: "100%",
                position: "relative",
                textAlign: "left",
              }}
            >
              <div style={{ width: "50%", position: "relative" }}>
                <h2>User it everywhere</h2>
                <p style={{ maxWidth: "80%" }}>
                  blah blahblahblahblahblah blahblahblah blahblah blah blah blah
                  blah blahblahblahblahblah blahblahblah blahblah blah blah blah
                  blah blahblahblahblahblah blahblahblah blahblah blah blah blah
                  blah blahblahblahblahblah blahblahblah blahblah blah blah blah
                </p>
              </div>
              <div style={{ width: "50%", position: "relative" }}>
                <h2>User it everywhere</h2>
                <p style={{ maxWidth: "80%" }}>
                  blah blahblahblahblahblah blahblahblah blahblah blah blah blah
                  blah blahblahblahblahblah blahblahblah blahblah blah blah blah
                  blah blahblahblahblahblah blahblahblah blahblah blah blah blah
                  blah blahblahblahblahblah blahblahblah blahblah blah blah blah
                </p>
              </div>
            </div>
            <div
              style={{ width: "100%", height: "100%", position: "relative" }}
            >
              <img
                style={{ width: "100%", height: 600, position: "relative" }}
                src={Woman}
              />
            </div>
          </div>
        </div>
        <div className={classes.sectionDark}>
          <h1 style={{ margin: 0 }}>test</h1>
        </div>
      </div>
    );
  }
}

export default withStyles(useStyles)(Landing);
